import React from 'react';
import { inject, observer } from 'mobx-react';
import { View, Text, Image, TouchableOpacity } from 'react-native';
import Carousel from 'react-native-snap-carousel';
import { SquareItem } from '../../components/SquareItem';
import { SQUARED } from '../../static/entries';
import styles from './styleSheet';

function SquareWrapperComponent({ data, save, active }) {
  _renderItem = ({item, index}) => {
    return (
      <SquareItem active={active} save={save} {...item} />
    );
  }
  return (
    <View style={styles.container}>
      <Carousel
        ref={(c) => { this._carousel = c; }}
        data={data}
        renderItem={this._renderItem}
        sliderWidth={300}
        itemWidth={300}
      />
    </View>
  );
}

class SquareWrapper extends React.Component {
  componentDidMount() {
    const {
      rootStore: {
        squaredStore: {
          getSquared
        }
      },
    } = this.props;
    getSquared();
  }

  render() {
    const {
      rootStore: {
        squaredStore: {
          squared,
          saveSquared,
          squareActive
        }
      },
    } = this.props;

    return <SquareWrapperComponent data={squared} save={saveSquared} active={squareActive} />;
  }
}

export default inject("rootStore")(observer(SquareWrapper));
